import { useState, useEffect } from 'react'
import { EventModal } from './addEventModal/addEventModal'
import './addEventModal/addEventModal.css'
import { Wrapper, CalenderHead, SevenColGrid, HeadDay, CalenderBody, StyledDay, StyledEvent } from './styled'
import 'bootstrap/dist/css/bootstrap.min.css'
import * as Icon from 'react-bootstrap-icons'
import { Days, MONTHS } from './const'
import { range, getDaysInMonth, getSortedDays, areDatesTheSame, getDateobj } from './util'

export const Calender = ({
  startingDate,
  eventsArr,
  addEvent,
  editEvent,
  removeEvent
}) => {
  const [currentMonth, setCurrentMonth] = useState(startingDate.getMonth())
  const [currentYear, setCurrentYear] = useState(startingDate.getFullYear())
  const DaysInMonth = getDaysInMonth(currentMonth, currentYear)
  const [selectedDay, setSelectedDay] = useState(new Date().getDay())
  const [selectedEvent, setSelectedEvent] = useState(null)
  const [eventForm, setEventForm] = useState({})
  const [showDeleteButton, setShowDeleteButton] = useState(false)
  const [modalType, setModalType] = useState('add') // 'add' or 'edit'

  const [showModal, setShowModal] = useState(false)

  const toggleModal = () => {
    setShowModal(!showModal)
  }

  const nextMonth = () => {
    if (currentMonth < 11) {
      setCurrentMonth(prev => prev + 1)
    } else {
      setCurrentMonth(0)
      setCurrentYear(prev => prev + 1)
    }
  }

  const prevMonth = () => {
    if (currentMonth > 0) {
      setCurrentMonth(prev => prev - 1)
    } else {
      setCurrentMonth(11)
      setCurrentYear(prev => prev - 1)
    }
  }

  const loadData = thisevent => {
    const eventOnDay = eventsArr.find(
      event =>
        event.title === thisevent.title &&
        event.date === thisevent.date &&
        event.reminderTime === thisevent.reminderTime
    )
    setEventForm(eventOnDay || null)
    setSelectedEvent(eventOnDay)
  }

  const onAddEditEvent = (event, data, selectedDay) => {
    event.preventDefault()
    if (modalType === 'edit') {
      editEvent(selectedEvent, data)
      setSelectedEvent(null)
    } else {
      addEvent(data, selectedDay)
    }
    toggleModal()
    setEventForm(null)
  }

  const onRemoveEvent = eventToBeDel => {
    removeEvent(eventToBeDel)
    toggleModal()
    setEventForm(null)
    setSelectedEvent(null)
  }

  const openAddEventModal = () => {
    setModalType('add')
    setEventForm({})
    toggleModal()
  }

  const openEditEventModal = (event) => {
    setModalType('edit')
    loadData(event)
    setShowDeleteButton(true)
    toggleModal()
  }

  return (
    <Wrapper>
      <CalenderHead>
        <Icon.ArrowLeftCircleFill onClick={prevMonth} />
        <p className='m-0'>
          {MONTHS[currentMonth]} {currentYear}
        </p>
        <Icon.ArrowRightCircleFill onClick={nextMonth} />
      </CalenderHead>
      <SevenColGrid>
        {getSortedDays(currentMonth, currentYear).map(day => (
          <HeadDay key={day}>{day}</HeadDay>
        ))}
      </SevenColGrid>
      <CalenderBody fourCol={DaysInMonth === 28}>
        {range(DaysInMonth).map(day => {
          const dateObj = getDateobj(day, currentMonth, currentYear)
          const isCurrentDate = areDatesTheSame(new Date(), dateObj)
          const eventsOnDate = eventsArr.filter(event =>
            areDatesTheSame(dateObj, event.date)
          )
          return (
            <StyledDay
              key={day}
              onClick={() => {
                setSelectedDay(getDateobj(day, currentMonth, currentYear))
                setEventForm(null)
                setShowDeleteButton(false)
                openAddEventModal()
              }}
              active={isCurrentDate}
            >
              <p>{day}</p>
              {eventsOnDate.map(event => (
                <StyledEvent key={event.title}>
                  <span
                    className='w-100 text-start'
                    type='button'
                    onClick={e => {
                      e.stopPropagation()
                      openEditEventModal(event)
                    }}
                  >
                    {event.title}
                  </span>
                </StyledEvent>
              ))}
            </StyledDay>
          )
        })}
      </CalenderBody>
      {showModal && (
        <div className='modal'>
          <div onClick={toggleModal} className='overlay'>
            <div className='modal-content' onClick={e => e.stopPropagation()}>
              <h4 className='form-heading'>
                {modalType === 'add' ? 'Add Event' : 'Edit Event'}
              </h4>
              <button
                type='button'
                className='close-btn'
                onClick={toggleModal}
              >
                <Icon.XLg />
              </button>
              {modalType === 'edit' && showDeleteButton && (
                <button
                  className='delete-btn'
                  onClick={e => {
                    e.stopPropagation()
                    onRemoveEvent(eventForm)
                  }}
                >
                  <Icon.Trash3 />
                </button>
              )}
              <form>
                <div className='form-group mb-3'>
                  <label className='form-label'>Title</label>
                  <input
                    type='text'
                    className='form-control'
                    value={eventForm?.title ?? ''}
                    onChange={e =>
                      setEventForm({ ...eventForm, title: e.target.value })
                    }
                    disabled={modalType === 'edit'}
                  />
                </div>
                <div className='form-group mb-3'>
                  <label className='form-label'>Description</label>
                  <textarea
                    className='form-control'
                    rows='3'
                    value={eventForm ? eventForm.desc : ''}
                    onChange={e =>
                      setEventForm({ ...eventForm, desc: e.target.value })
                    }
                    placeholder='Event description'
                    required
                  />
                </div>
                <div className='d-flex justify-content-end'>
                  <button
                    className='save-event'
                    onClick={e => {
                      e.stopPropagation()
                      onAddEditEvent(e, eventForm, selectedDay)
                    }}
                  >
                    Save
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </Wrapper>
  )
}
