import styled from "styled-components";

export const Wrapper = styled.div`
width: 99%;
height: 100vh;
border: 1px solid black;
margin: 10px;
`;

export const CalenderHead = styled.div`
width: 100%;
height: 40px;
display: flex;
justify-content: space-around;
align-items: center;
font-size: 20px;
`;

export const SevenColGrid = styled.div`
width: 100%;
display: grid;
height: 27px;
grid-template-columns: repeat(7, 1fr);
`;

export const HeadDay = styled.span`
text-align: center;
background: #f0a95d;
font-size: 1.2rem;
`;

export const CalenderBody = styled.div`
width: 100%;
height: calc(100% - 40px - 27px);
display: grid;
grid-template-columns: repeat(7, 1fr);
grid-template-rows: repeat(${({fourCol}) => fourCol ? 4 : 5}, 1fr);
`;

export const StyledDay = styled.span`
border: 1px solid black;
text-align: right;
padding: 10px;
${({active}) => active && `background: #a876f5;`}
`;

export const StyledEvent = styled.span`
display: flex;
align-items: center;
width: 100%;
background: rgb(118 204 245);
padding: 2px 10px;
margin-bottom: 5px;
border: 1px solid #e3e3e3;
border-radius: 20px;
`;

export const PortalWrapper = styled.div`
  background: white;
  position: absolute;
  width: 60%;
  height: 200px;
  top: 50%;
  left: 50%;
  border-radius: 6px;
  transform: translate(-50%, -50%);
  box-shadow: 10px 10px 20px black;
  padding: 40px;

  h2 {
    font-size: 3rem;
  }

  ion-icon {
    font-size: 2rem;
    color: red;
    background: lightblue;
    padding: 10px 20px;
    border-radius: 6px;
  }

  p {
    margin-bottom: 15px;
  }

  ion-icon[name='close-outline'] {
    position: absolute;
    top: 10px;
    right: 10px;
    background: red;
    color: lightblue;
  }

  input {
    width: 100%;
    height: 30px;
    padding: 5px;
    margin-bottom: 10px;
  }

  button {
    background: lightblue;
    color: black;
    padding: 8px 12px;
    border-radius: 4px;
    margin-right: 10px;
    cursor: pointer;
  }
`
