import React, { useState, useEffect } from 'react';
import {
  Wrapper,
  CalenderHead,
  SevenColGrid,
  HeadDay,
  CalenderBody,
  StyledDay,
  StyledEvent
} from './styled';
import 'bootstrap/dist/css/bootstrap.min.css';

import * as Icon from 'react-bootstrap-icons';
import { Days, MONTHS } from './const';
import {
  range,
  getDaysInMonth,
  getSortedDays,
  areDatesTheSame,
  getDateobj
} from './util';
import { EventModal } from './addEventModal/addEventModal';
import './addEventModal/addEventModal.css';

export const Calender = ({
  startingDate,
  eventsArr,
  addEvent,
  editEvent,
  removeEvent
}) => {
  const [currentMonth, setCurrentMonth] = useState(startingDate.getMonth());
  const [currentYear, setCurrentYear] = useState(startingDate.getFullYear());
  const DaysInMonth = getDaysInMonth(currentMonth, currentYear);
  const [selectedDay, setSelectedDay] = useState(new Date().getDay());
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [eventForm, setEventForm] = useState({
    title: '',
    time: '',
    reminder: ''
  });
  const reminderOptions = [
    { label: '5 minutes before', value: '5' },
    { label: '10 minutes before', value: '10' }
    // Add more options as needed
  ];

  const [showDeleteButton, setShowDeleteButton] = useState(false);
  const [Modal, setModal] = useState(false);
  const [showReminder, setShowReminder] = useState(false); // State to control the visibility of the reminder toaster

  const toggleModal = () => {
    setModal(!Modal);
  };

  const nextMonth = () => {
    if (currentMonth < 11) {
      setCurrentMonth(prev => prev + 1);
    } else {
      setCurrentMonth(0);
      setCurrentYear(prev => prev + 1);
    }
  };

  const prevMonth = () => {
    if (currentMonth > 0) {
      setCurrentMonth(prev => prev - 1);
    } else {
      setCurrentMonth(11);
      setCurrentYear(prev => prev - 1);
    }
  };

  const loadData = thisevent => {
    const eventOnDay = eventsArr.find(
      event =>
        event.title === thisevent.title &&
        event.date === thisevent.date &&
        event.time === thisevent.time
    );
    setEventForm(eventOnDay ? { ...eventOnDay } : null);
    setSelectedEvent(eventOnDay ? { ...eventOnDay } : null);
  };

  const onAddEditEvent = (event, data, selectedDay) => {
    event.preventDefault();
    const currentTime = new Date().toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit'
    });

    if (!data.time && selectedEvent) {
      // If event time is empty and editing an existing event, set the time to current time
      data.time = currentTime;
    }

    if (data.time >= currentTime) {
      if (selectedEvent) {
        editEvent(selectedEvent, data);
        setSelectedEvent(null);
      } else {
        addEvent(data, selectedDay);
      }
      toggleModal();
      setEventForm(null);
    } else {
      alert('Event time should be greater than the current time.');
    }
  };

  const onRemoveEvent = eventToBeDel => {
    removeEvent(eventToBeDel);
    toggleModal();
    setEventForm(null);
    setSelectedEvent(null);
  };

  useEffect(() => {
    // Check if any event has a reminder and schedule the reminder toaster
    const hasReminder = eventsArr.some(event => !!event.reminder);
    if (hasReminder) {
      const reminderTimer = setInterval(() => {
        const currentTime = new Date().toLocaleTimeString([], {
          hour: '2-digit',
          minute: '2-digit'
        });

        eventsArr.forEach(event => {
          if (event.time === currentTime && event.reminder) {
            // Show the reminder toaster when the event time matches the current time
            setShowReminder(true);
          }
        });
      }, 1000); // Check every second for event reminders

      return () => {
        // Clear the interval when the component unmounts
        clearInterval(reminderTimer);
      };
    }
  }, [eventsArr]);

  return (
    <Wrapper>
      <CalenderHead>
        <Icon.ArrowLeftCircleFill onClick={prevMonth} />
        <p className='m-0'>
          {MONTHS[currentMonth]} {currentYear}
        </p>
        <Icon.ArrowRightCircleFill onClick={nextMonth} />
      </CalenderHead>
      <SevenColGrid>
        {getSortedDays(currentMonth, currentYear).map(day => (
          <HeadDay>{day}</HeadDay>
        ))}
      </SevenColGrid>
      <CalenderBody fourCol={DaysInMonth === 28}>
        {range(DaysInMonth).map(day => {
          const dateObj = getDateobj(day, currentMonth, currentYear);
          const isCurrentDate = areDatesTheSame(new Date(), dateObj);
          const eventsOnDate = eventsArr.filter(event =>
            areDatesTheSame(dateObj, event.date)
          );
          return (
            <>
              <StyledDay
                onClick={e => {
                  setSelectedDay(getDateobj(day, currentMonth, currentYear));
                  setEventForm(null);
                  setShowDeleteButton(false);
                  setSelectedEvent(null); // Clear the selectedEvent state for new events
                  toggleModal();
                }}
                active={areDatesTheSame(
                  new Date(),
                  getDateobj(day, currentMonth, currentYear)
                )}
              >
                <p>{day}</p>
                {eventsArr.map(
                  event =>
                    areDatesTheSame(
                      getDateobj(day, currentMonth, currentYear),
                      event.date
                    ) && (
                      <StyledEvent>
                        <span
                          className='w-100 text-start'
                          type='button'
                          onClick={e => {
                            e.stopPropagation();
                            loadData(event);
                            setShowDeleteButton(true);
                            toggleModal();
                          }}
                        >
                          {event.title}
                        </span>
                      </StyledEvent>
                    )
                )}
              </StyledDay>
              {Modal && (
                <div className='modal'>
                  <div onClick={toggleModal} className='overlay'>
                    <div
                      className='modal-content'
                      onClick={e => e.stopPropagation()}
                    >
                      <h4 className='form-heading'>
                        {selectedEvent ? 'Edit Event' : 'Add Event'}
                      </h4>

                      <button
                        type='button'
                        className='close-btn'
                        onClick={toggleModal}
                      >
                        <Icon.XLg />
                      </button>

                      <form>
                        <div className='form-group mb-3'>
                          <h4>
                            <label className='form-label'>Title</label>
                          </h4>
                          <input
                            type='text'
                            className='form-control'
                            value={eventForm?.title || ''}
                            onChange={e =>
                              setEventForm({
                                ...eventForm,
                                title: e.target.value
                              })
                            }
                          />
                        </div>

                        <div className='form-group mb-3'>
                          <h4>
                            <label className='form-label'> EventTime</label>
                          </h4>
                          <input
                            type='time'
                            className='form-control'
                            value={eventForm?.time || ''}
                            onChange={e =>
                              setEventForm({
                                ...eventForm,
                                time: e.target.value
                              })
                            }
                            required
                          />
                        </div>
                        <div className='form-group mb-3'>
                          <h4>
                            <label className='form-label'>Reminder</label>
                          </h4>
                          <select
                            className='form-control'
                            value={eventForm?.reminder || ''}
                            onChange={e =>
                              setEventForm({
                                ...eventForm,
                                reminder: e.target.value
                              })
                            }
                          >
                            <option value=''>No Reminder</option>
                            {reminderOptions.map(option => (
                              <option key={option.value} value={option.value}>
                                {option.label}
                              </option>
                            ))}
                          </select>
                        </div>

                        {/* ... */}
                        <div className='d-flex justify-content-end'>
                          <button
                            className='save-event'
                            disabled={!eventForm?.title} // Disable the button if the title is not filled
                            style={{
                              opacity: eventForm?.title ? 1 : 0.5,
                              cursor: eventForm?.title
                                ? 'pointer'
                                : 'not-allowed'
                            }}
                            onClick={e => {
                              e.stopPropagation();
                              onAddEditEvent(e, eventForm, selectedDay);
                            }}
                          >
                            Save
                          </button>

                          {showDeleteButton && (
                            <button
                              className='delete-btn'
                              onClick={e => {
                                e.stopPropagation();
                                onRemoveEvent(eventForm);
                              }}
                            >
                              Delete
                            </button>
                          )}
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              )}
            </>
          );
        })}
      </CalenderBody>
      {showReminder && (
        <div className='reminder-toast'>
          <p>Event Reminder</p>
        </div>
      )}
    </Wrapper>
  );
};
